mapper.update:
update talat_memb tm
  left outer join talat_join tj
  on tm.mno=tj.mno

  tm.mname=#{mname}
  tm.email=#{email}
  tm.tel=#{tel}
  tm.birth=#{birth}
  tj.nic=#{nickName}
  tj.pst_no=#{postNo}
  tj.basic_addr=#{basicAddress}
  tj.det_addr=#{detailAddress}
  tj.pref_gender=#{preferenceGender}

where tm.mno=#{mno}
